-- Docente
CREATE TABLE Docente (
    docente_DNI NUMERIC(8,0) PRIMARY KEY NOT NULL,
    Nombre VARCHAR(100),
    ApellidoPaterno VARCHAR(100),
    ApellidoMaterno VARCHAR(100),
    FechaNacimiento DATE,
    Colegiatura VARCHAR(50),
    Clave VARCHAR(100) NOT NULL  
);

-- Tutor
CREATE TABLE Tutor (
    tutor_DNI NUMERIC(8,0) PRIMARY KEY NOT NULL,
    Nombre VARCHAR(100),
    ApellidoPaterno VARCHAR(100),
    ApellidoMaterno VARCHAR(100)
);

-- Nivel
CREATE TABLE Nivel (
    nivel_ID SERIAL PRIMARY KEY,
    Nombre VARCHAR(20)
);

-- Grado
CREATE TABLE Grado (
    grado_ID SERIAL PRIMARY KEY,
    Nivel_ID INT REFERENCES Nivel(nivel_ID),
    Nombre VARCHAR(20)
);

-- Seccion
CREATE TABLE Seccion (
    seccion_ID SERIAL PRIMARY KEY,
    Grado_ID INT REFERENCES Grado(grado_ID),
    Nombre VARCHAR(20)
);

-- Materia
CREATE TABLE Materia (
    materia_ID SERIAL PRIMARY KEY,
    Nombre VARCHAR(100),
    Nivel_ID INT,
    Grado_ID INT,
    Seccion_ID INT,
    FOREIGN KEY (Nivel_ID) REFERENCES Nivel(nivel_ID),
    FOREIGN KEY (Grado_ID) REFERENCES Grado(grado_ID),
    FOREIGN KEY (Seccion_ID) REFERENCES Seccion(seccion_ID)
);

-- DocenteMateria (Nueva tabla de asignación)
CREATE TABLE DocenteMateria (
    doce_mate_ID SERIAL PRIMARY KEY,
    Docente_DNI NUMERIC(8,0) REFERENCES Docente(docente_DNI),
    Materia_ID INT REFERENCES Materia(materia_ID)
);

-- Alumno
CREATE TABLE Alumno (
    alumno_DNI NUMERIC(8,0) PRIMARY KEY NOT NULL,
    Nombre VARCHAR(100),
    ApellidoPaterno VARCHAR(100),
    ApellidoMaterno VARCHAR(100),
    FechaNacimiento DATE,
    Tutor_DNI NUMERIC(8,0) REFERENCES Tutor(tutor_DNI),
    Clave VARCHAR(100) NOT NULL 
);

-- CalificacionTrimestral
CREATE TABLE CalificacionTrimestral (
    cali_tri_ID SERIAL PRIMARY KEY,
    Alumno_DNI NUMERIC(8,0) REFERENCES Alumno(alumno_DNI),
    Materia_ID INT REFERENCES Materia(materia_ID),
    Docente_DNI NUMERIC(8,0) REFERENCES Docente(docente_DNI),
    Trimestre INT CHECK (Trimestre BETWEEN 1 AND 3),
    Calificacion DECIMAL(4,2) DEFAULT NULL
);

-- CalificacionBimestral
CREATE TABLE CalificacionBimestral (
    cali_bi_ID SERIAL PRIMARY KEY,
    Alumno_DNI NUMERIC(8,0) REFERENCES Alumno(alumno_DNI),
    Materia_ID INT REFERENCES Materia(materia_ID),
    Docente_DNI NUMERIC(8,0) REFERENCES Docente(docente_DNI),
    Bimestre INT CHECK (Bimestre BETWEEN 1 AND 4),
    Calificacion DECIMAL(4,2) DEFAULT NULL
);

-- Asistencia
CREATE TABLE Asistencia (
    asistencia_ID SERIAL PRIMARY KEY,
    Alumno_DNI NUMERIC(8,0) REFERENCES Alumno(alumno_DNI),
    Materia_ID INT REFERENCES Materia(materia_ID),
    Docente_ID INT REFERENCES Docente(docente_DNI),
    Fecha DATE,
    Asistio BOOLEAN DEFAULT TRUE
);

CREATE TABLE AlumnoMateria(
alum_mate_ID SERIAL PRIMARY KEY,
Alumno_DNI NUMERIC(8,0) REFERENCES Alumno(alumno_DNI),
Materia_ID INT REFERENCES Materia(materia_ID)
);
